import { Bud } from '@roots/bud-framework';
/**
 * Execute a function after compiler has finished
 */
export const after = function (fn, errorHandler) {
    this.hooks.action(`compiler.done`, async (bud) => {
        try {
            await bud.promise(fn);
        }
        catch (error) {
            if (!errorHandler)
                throw error;
            errorHandler(error);
        }
    });
    return this;
};
